  --destructive: 0 75% 45%;

  --destructive-foreground: 0 75% 98%;

  --input: 210 15% 75%;
  --ring: 145 70% 35%;
  --chart-1: 145 70% 35%;
  --chart-2: 200 70% 45%;
  --chart-3: 80 50% 40%;
  --chart-4: 210 60% 50%;
  --chart-5: 30 75% 50%;

  --font-sans: 'IBM Plex Sans', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'IBM Plex Mono', Menlo, monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(210 15% 15% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(210 15% 15% / 0.08);
  --shadow-sm: 0px 2px 4px 0px hsl(210 15% 15% / 0.06), 0px 1px 2px -1px hsl(210 15% 15% / 0.04);
  --shadow: 0px 4px 6px 0px hsl(210 15% 15% / 0.07), 0px 2px 4px -1px hsl(210 15% 15% / 0.05);
  --shadow-md: 0px 6px 8px 0px hsl(210 15% 15% / 0.08), 0px 3px 6px -2px hsl(210 15% 15% / 0.06);
  --shadow-lg: 0px 10px 15px 0px hsl(210 15% 15% / 0.10), 0px 4px 8px -2px hsl(210 15% 15% / 0.08);
  --shadow-xl: 0px 20px 25px 0px hsl(210 15% 15% / 0.12), 0px 8px 12px -4px hsl(210 15% 15% / 0.10);
  --shadow-2xl: 0px 25px 50px 0px hsl(210 15% 15% / 0.15);
  --tracking-normal: 0em;
